export default function convertDate(apiDate) {
  // let d = new Date(apiDate);
  let d = apiDate;
  // let date = d.getDate();
  let date = d.substring(0, 4)
  let month = d.getMonth();
  let year = d.getFullYear();
  let dateStr = `${pickMonth(month)} ${date}, ${year}`;

  console.log("1: ", apiDate)
  console.log("2: ", d)
  console.log("3: ", d.toLocaleDateString())
  console.log("4: ", new Date())

  return dateStr
}

const months = ['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December'];

function pickMonth(monthNum) {
  for (let i = 0; i < months.length -1; i++) {
    if (monthNum === i) {
      return months[i]
    }
  }
}
