import { useState } from 'react';

import useStats from '../utils/useStats';
import Stats from './Stats';




export default function CountrySelector() {

  function updateKeyNum() {
    setKeyNum(keyNumkeyNum++);
    return keyNum;
  }

  console.log(updateKeyNum)

  const [keyNum, setKeyNum] = useState(1);
  const {stats: countries, loading, error} = useStats('https://covid19.mathdro.id/api/countries');
  const [selectedCountry, setSelectedCountry] = useState('CAN');
  if (!countries) return <p>Loading...</p>
  if (loading) return <p>Loading...</p>
  if (error) return <p>Loading...</p>

  // console.log(updateKeyNum());
  return (
    <div>
      <h2>Currently Showing {selectedCountry}</h2>
      <select onChange={e => {
        setSelectedCountry(e.target.value);
      }}>
        {Object.entries(countries.countries).map(([country, code]) => (
          <option selected={selectedCountry === countries.iso3[code]} key={code + setKeyNum(keyNum + 1)} value={countries.iso3[code]}>{country}</option>
        ))}
      </select>
      <Stats url={`https://covid19.mathdro.id/api/countries/${selectedCountry}`} />
    </div>
  )


}
